import styled from "@emotion/styled";

export const Card = styled.section`
 width: 400px;
  border-radius: 5px;
  background-color: #fff;
  margin-top: 20px;
  border: 1px solid #000000;
`;

export const Title = styled.h2`
font-size: 24px;
  text-align: center;
`;

export const Statlist = styled.ul`
 list-style: none;
  display: flex;
  padding: 0;
  margin-bottom: 0;
`;
