import styled from "@emotion/styled";

export const Table = styled.table `
   box-shadow: 5px 5px 5px;

  width: 20%;
  background-color: #ffffff;
  border-collapse: collapse;
  border-width: 1px;
  border-color: #000000;
  border-style: solid;
  color: #000000;

td,
th {
  border-width: 1px;
  border-color: #000000;
  border-style: solid;
  padding: 3px;
}
thead {
  background-color: #beb4e4;
}  
`
